    def generate_augmented_samples(self, src: str, tgt: str, category: str, num_variants: int = 5) -> List[Dict]:
        """
        根据场景分类生成增强数据
        :param src: 源词
        :param tgt: 目标词
        :param category: 场景标签 (如 'medical', 'tech')
        :param num_variants: 生成变体数量
        """
        samples = []
        
        # 1. 基础样本 (纯词条，必须保留)
        samples.append({"src": src, "tgt": tgt})
        
        # 2. 获取对应场景的模板
        templates = self.scene_templates.get(category, self.scene_templates["abstract"])
        
        # 3. 随机选择模板生成句子
        # 如果请求数量多于现有模板数，则使用所有模板；否则随机采样
        selected_templates = random.sample(templates, min(num_variants, len(templates)))
        
        for eng_tpl, zho_tpl in selected_templates:
            # 格式化字符串，填入术语
            aug_src = eng_tpl.format(src=src)
            aug_tgt = zho_tpl.format(tgt=tgt)
            samples.append({"src": aug_src, "tgt": aug_tgt})
            
        return samples

