    def save_datasets(self, datasets: Dict[str, List]):
        """保存数据集到文件"""
        for split, data in datasets.items():
            output_path = f"{self.output_dir}/{split}/data.json"
            
            with open(output_path, 'w', encoding='utf-8') as f:
                json.dump(data, f, ensure_ascii=False, indent=2)
            
            print(f"✅ {split.upper()} 数据集已保存: {output_path} ({len(data)} 样本)")

    def export_terminology_dict(self, output_path: str = "data/terminology.json"):
        """导出纯术语字典 (src -> tgt)"""
        # 只导出三元组中的术语，不包含手动添加的长句
        term_dict = {item[0]: item[1] for item in self.terminology_triplets}
        
        with open(output_path, 'w', encoding='utf-8') as f:
            json.dump(term_dict, f, ensure_ascii=False, indent=2)
        print(f"✅ 术语词典已导出: {output_path}")


